#!/bin/sh
#
# Please edit the following lines to set your preferences and then run this script.
# To run the script type 'sudo bash ./install-openhab-arm64.sh |& tee install.log' without the quotes to send a full copy of the terminal output to file.
# Type 'sudo bash ./install-openhab-arm64.sh' without the quotes to not output to file as it may fix any display errors.
#
####################### PREFERENCES #####################
# Enter path to where the latest Zulu 32bit+HF JVM file can be downloaded from. This one has been tested to work last time I ran the script.
ZuluFileURL=http://cdn.azul.com/zulu-embedded/bin/zulu8.38.0.163-ca-jdk1.8.0_212-linux_aarch32hf.tar.gz

#Name of just the file from the link above without the filename extension.
ZuluFileName=zulu8.38.0.163-ca-jdk1.8.0_212-linux_aarch32hf

#You should not login as root after this script is run. This is the username that will be created for you to logon from now on.
UserName=openhabian

#Hostname that you can use instead of an IP address. Defaults to openhabianpi as many parts of the documentation refer to links using this.
HostName=openhabianpi

#Install ffmpeg for IpCameras.
InstallFFMPEG=true

#Install openhabian which will not be used to install openhab2, but can be run to access any features it contains.
InstallOpenhabian=false

#This is very handy to watch your log files through a browser.
InstallFrontail=true

#This options sends the openhab log files and some other files to a RAM based location that will wipe the files clean on a reboot
#This is done to reduce corrupt flash storage when the power fails in the middle of a SD card write.
ReduceDiskWrites=true

# Specify when the Blue LED should flash, some options are 'none','sd','emmc','heartbeat'
BlueLEDflashesOn=sd

#Install the addon shield RTC to keep the time accurate when net connection goes down
#Leave this as false unless you know what this is. It is an optional extra purchase.
SetupRTC=false

#Options for a MQTT broker. If you dont want it to install change the next line to false
InstallMosquitto=true
MosquittoUserName=openhabian

################## Do NOT change any lines below here, all options that may need to be changed should be above this line ###################
clear
read -n1 -r -p "Have you changed the password for the user root? Press y if you have, otherwise press any other key and we will change the password now." key
if [ "$key" != "y" ]; then
echo "The key you pressed was $key, Please enter the new root password now."
sudo passwd root
fi
if ! id -u $UserName > /dev/null 2>&1; then
clear
echo
echo "It is not a good idea to login and use root for day to day use, so we will now setup a new user for you to use from now on."
echo "Use this new user called $UserName to login with via SSH instead of root. It will ask you to type your password in twice"
echo "You can press enter to skip the name and address details after the password."
adduser $UserName
fi
sudo sh -ce "usermod -aG sudo $UserName"

################ Install Zulu Java ###############
clear
echo "Now install the latest Linux updates."
dpkg --add-architecture armhf
sudo apt-get update && sudo apt-get dist-upgrade -y
sudo apt-get install -y mc nano build-essential udev:armhf software-properties-common
clear
echo "Now installing Zulu Java VM"
mkdir /usr/lib/jvm
wget $ZuluFileURL
tar xvzf $ZuluFileName".tar.gz" -C /usr/lib/jvm/
rm $ZuluFileName".tar.gz"
sudo update-alternatives --install /usr/bin/java java /usr/lib/jvm/$ZuluFileName/bin/java 1101
echo
echo 'Select the zulu JVM from the list if multiple choices are shown'
sudo update-alternatives --config java

################ SETUP JAVA HOME #################
echo "Setting the JAVA_HOME enviroment variable now"
sudo sh -ce "echo 'JAVA_HOME=\"/usr/lib/jvm/$ZuluFileName\"' >> /etc/environment"
. /etc/environment
echo $JAVA_HOME

################ Install FFMPEG ##################
if [ "$InstallFFMPEG" == "true" ]; then
clear
echo "Now installing ffmpeg."
sudo apt-get install -y ffmpeg
fi

################ SETUP OPENHAB 2 #################
clear
echo "Performing Pre Openhab 2.x installation tasks"
sudo sh -ce "echo $HostName > /etc/hostname"
sudo hostname $HostName
wget -qO - 'https://bintray.com/user/downloadSubjectPublicKey?username=openhab' | sudo apt-key add -
echo 'deb https://dl.bintray.com/openhab/apt-repo2 stable main' | sudo tee /etc/apt/sources.list.d/openhab2.list
sudo apt-get update
sudo apt-get install -y screen mc vim git htop apt-transport-https curl
sudo locale-gen en_US.UTF-8
update-locale LANG=en_US.UTF-8
sudo dpkg-reconfigure tzdata
sudo apt-get install -y ntp
#2gb swapfile steps
sudo fallocate -l 2G /swapfile
sudo chmod 600 /swapfile
sudo mkswap /swapfile
sudo swapon /swapfile
sudo sh -ce "echo '/swapfile none swap sw 0 0' >> /etc/fstab"
sudo sysctl vm.swappiness=2
sudo sh -ce "echo 'vm.swappiness=2' >> /etc/sysctl.conf"
#Should be installed as everyday user and not root!
#sudo apt-get install -y openhab2
clear
echo "Openhab 2.x can not be installed as root and will now need your password for the user $UserName"
sudo -u $UserName sudo apt-get install -y openhab2
#following 3 worked when manually ran as everyday user, not yet tested via script...
sudo /bin/systemctl daemon-reload
sudo /bin/systemctl enable openhab2.service
sudo /bin/systemctl start openhab2.service
sudo adduser openhab dialout
sudo adduser openhab tty
sudo adduser openhab audio
setcap 'cap_net_raw,cap_net_admin=+eip cap_net_bind_service=+ep' $(realpath /usr/bin/java)

################ Install Openhabian #############
if [ "$InstallOpenhabian" == "true" ]; then
clear
echo "Now installing Openhabian"
sudo apt-get install -y git
sudo git clone https://github.com/openhab/openhabian.git /opt/openhabian
sudo ln -s /opt/openhabian/openhabian-setup.sh /usr/bin/openhabian-config
fi

if [ "$InstallFrontail" == "true" ]; then
clear
echo "Now installing Frontail"
curl -sL https://deb.nodesource.com/setup_8.x | sudo -E bash -
#Used to install npm at same time as nodejs
sudo apt-get install -y nodejs
sudo npm install -g frontail
#cp /opt/openhabian/includes/frontail-preset.json /usr/lib/node_modules/frontail/preset/openhab.json
#cp /opt/openhabian/includes/frontail-theme.css /usr/lib/node_modules/frontail/web/assets/styles/openhab.css
sudo cat << EOF > /usr/lib/node_modules/frontail/preset/openhab.json
{
  "words": {
    "[ERROR]": "color: red;",
    "[WARN ]": "color: orange;",
    "[INFO ]": "color: green;",
    "Node is DEAD": "color: red;",
    "[GroupItemStateChangedEvent]": "color: #0026FF; font-weight: bold;",
    "[ItemStateChangedEvent     ]": "color: #0026FF;",
    "[ItemCommandEvent          ]": "color: #00137F;",
    "[hingStatusInfoChangedEvent]": "color: #007F46;",
    "received command ON": "color: black;",
    "received command OFF": "color: black;",
    "received command": "color: black;"
  },
  "lines": {
    "/var/log/openhab2/openhab.log": "text-align: right; font-size: 0.8em; border-top: 2px solid #F8F8F8;",
    "/var/log/openhab2/events.log": "text-align: right; font-size: 0.8em; border-top: 2px solid #F8F8F8;",
    "ERROR": "background-color: #F3BFBF;",
    "model.script": "background-color: #A6CFFD; font-weight: bold;"
  }
}
EOF

sudo cat << EOF > /usr/lib/node_modules/frontail/web/assets/styles/openhab.css
@import "bootstrap.min.css";

body {
  padding-top: 7em;
}

.no-topbar {
  padding-top: 10px;
}

.navbar {
  background-color: #FF6600;
  border: 0;
  box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.15);
  padding-top: 15px;
  padding-bottom: 15px;
}

.navbar .navbar-brand {
  color: white;
}

.navbar .navbar-brand::before {
  content: "openHAB 2 Log Viewer (frontail)";
  font-size: 150%;
  padding-right: 30px;
}

.form-control {
  color: #a0a0a0;
}

.log {
  white-space: pre-wrap;
  color: #505050;
  font-size: .95em;
  background: inherit;
  border: 0;
  padding: 0;
}

.log .inner-line {
  padding: 0 15px;
  margin-left: 288pt;
  text-indent: -288pt;
  margin-bottom: 0;
}

.log .inner-line:empty::after {
  content: '.';
  visibility: hidden;
}

.log.no-indent .inner-line {
  margin-left: 0;
  text-indent: 0;
}

.log .line-selected {
  background-color: #e0e0e0;
}
EOF

sudo cat << EOF > /etc/systemd/system/frontail.service
[Unit]
Description=Frontail openHAB instance, reachable at http://%H:9001
Documentation=https://github.com/mthenw/frontail
After=openhab2.service

[Service]
ExecStart=/usr/lib/node_modules/frontail/bin/frontail --ui-highlight --ui-highlight-preset /usr/lib/node_modules/frontail/preset/openhab.json -t openhab -l 2000 -n 200 /var/log/openhab2/openhab.log /var/log/openhab2/events.log
Restart=always
User=openhab
Group=openhab
Environment=PATH=/usr/bin
Environment=NODE_ENV=production
WorkingDirectory=/usr/bin

[Install]
WantedBy=multi-user.target
Alias=frontail.service
EOF

sudo chmod 664 /etc/systemd/system/frontail.service
sudo /bin/systemctl daemon-reload
sudo /bin/systemctl enable frontail.service
sudo /bin/systemctl start frontail.service
# to fault find run this next line.
#sudo journalctl -u frontail.service -b

sudo sh -ce "echo 'frontail.link-name=openHAB Log Viewer' >> /etc/openhab2/services/dashboard.cfg"
sudo sh -ce "echo 'frontail.link-url=http://$HostName:9001' >> /etc/openhab2/services/dashboard.cfg"
sudo sh -ce "echo 'frontail.link-imageurl=data:image/png;base64,' >> /etc/openhab2/services/dashboard.cfg"
fi

#################### SETUP SAMBA SHARES ###################
clear
echo "Now installing SAMBA"
echo "Setting the SAMBA shares now, these allow you to see useful folders over the network."
sudo apt-get install -y samba samba-common-bin smbclient
sudo cat << EOF >> /etc/samba/smb.conf
[openHAB2-userdata]
  comment=openHAB2 userdata
  path=/var/lib/openhab2
  browseable=Yes
  writeable=Yes
  only guest=no
  public=no
  create mask=0777
  directory mask=0777

[openHAB2-conf]
  comment=openHAB2 site configuration
  path=/etc/openhab2
  browseable=Yes
  writeable=Yes
  only guest=no
  public=no
  create mask=0777
  directory mask=0777

[openHAB2-addons]
  comment=openHAB2 addons
  path=/usr/share/openhab2/addons
  browseable=Yes
  writeable=Yes
  only guest=no
  public=no
  create mask=0777
  directory mask=0777

EOF
echo
echo "You will now be asked to set a SAMBA password for USER $UserName which you will need to remember this password to connect to the network shares."
sudo smbpasswd -a $UserName
#sudo smbpasswd -e $UserName
#Add user to the openhab group so it can access the shares.
sudo usermod -a -G openhab $UserName
sudo chown -hR openhab:openhab /etc/openhab2 /var/lib/openhab2 /usr/share/openhab2
sudo chmod -R 770 /etc/openhab2 /var/lib/openhab2 /usr/share/openhab2

#testparm
sudo service restart smbd.service

################ SETUP MOSQUITTO MQTT BROKER ##############
if [ "$InstallMosquitto" == "true" ]
then
sudo apt-get install -y mosquitto mosquitto-clients
echo
echo "You will now be asked to set a MQTT password for USER $MosquittoUserName which you will need to remeber this password to connect to MQTT."
mosquitto_passwd -c /etc/mosquitto/passwd $MosquittoUserName
#sudo cat << EOF >> /etc/mosquitto/mosquitto.conf
#replace entire file as above command did not work every time
sudo cat << EOF > /etc/mosquitto/mosquitto.conf
pid_file /var/run/mosquitto.pid
persistence true
autosave_on_changes true
autosave_interval 1800
persistence_location /var/lib/mosquitto/
persistent_client_expiration 7d
log_dest file /tmpfs/mosquitto.log
persistence_file mosquitto.db
log_dest stdout
connection_messages false
log_timestamp true
allow_anonymous false
password_file /etc/mosquitto/passwd
EOF
sudo service mosquitto restart
fi

############## SETUP RTC ########################
if [ "$SetupRTC" == "true" ]
then
#install RTC support
sudo apt-get install -y device-tree-compiler
echo "#RTC fix"|sudo tee -a /etc/modprobe.d/blacklist.conf
echo "blacklist sx865x"|sudo tee -a /etc/modprobe.d/blacklist.conf
#sudo fdtput -t s /boot/meson64_odroidc2.dtb /i2c@c1108500/pcf8563@51 status "okay"
sudo fdtput -t s /media/boot/meson64_odroidc2.dtb /i2c@c1108500/pcf8563@51 status "okay"
echo "aml_i2c"|sudo tee -a /etc/modules
echo "rtc_pcf8563"|sudo tee -a /etc/modules
echo "aml_i2c"|sudo tee -a /etc/modules-load.d/aml_i2c.conf
modprobe -a aml_i2c rtc_pcf8563

#make the RTC load the date/time on each boot
sudo cat << EOF > /etc/rc.local
if [ -f /aafirstboot ]; then /aafirstboot start ; fi
hwclock -s
exit 0
EOF

#patch to stop Kernal updates breaking the RTC
echo '#!/bin/sh' > /etc/kernel/postinst.d/rtc
#echo 'fdtput -t s /boot/meson64_odroidc2.dtb /i2c@c1108500/pcf8563@51 status "okay"' >> /etc/kernel/postinst.d/rtc
echo 'fdtput -t s /media/boot/meson64_odroidc2.dtb /i2c@c1108500/pcf8563@51 status "okay"' >> /etc/kernel/postinst.d/rtc
chmod +x /etc/kernel/postinst.d/rtc
fi

############### Reduce SD/eMMC disk writes #######
if [ "$ReduceDiskWrites" == "true" ]
then
#Send systemd journal to ram instead of disk. Deleting file will return all defaults.
sudo sed -i -e 's/#Storage=auto/Storage=volatile/g' /etc/systemd/journald.conf
#create a tmp folder in ram to redirect log files there.
sudo sh -ce "echo 'tmpfs /tmpfs tmpfs defaults,nosuid,nodev,noatime,size=100m 0 0' >> /etc/fstab"
sudo sed -i -e 's/exit 0/#exit 0/g' /etc/rc.local
sudo sh -ce "echo 'chown openhab:openhab /tmpfs/' >> /etc/rc.local"
sudo sh -ce "echo 'ln -sf /tmpfs/events.log /var/log/openhab2/events.log' >> /etc/rc.local"
sudo sh -ce "echo 'ln -sf /tmpfs/openhab.log /var/log/openhab2/openhab.log' >> /etc/rc.local"
sudo sh -ce "echo 'ln -sf /tmpfs/syslog /var/log/syslog' >> /etc/rc.local"
sudo sh -ce "echo 'ln -sf /tmpfs/daemon.log /var/log/daemon.log' >> /etc/rc.local"
sudo sh -ce "echo 'ln -sf /tmpfs/auth.log /var/log/auth.log' >> /etc/rc.local"
#change blue LED to flash on SD access, need to give option on what flash is used...
sudo sh -ce "echo 'echo $BlueLEDflashesOn >/sys/class/leds/blue\:heartbeat/trigger' >> /etc/rc.local"
sudo sh -ce "echo 'exit 0' >> /etc/rc.local"
#Needed so frontail can still find the files
sudo ln -sf /tmpfs/events.log /var/log/openhab2/events.log
sudo ln -sf /tmpfs/openhab.log /var/log/openhab2/openhab.log
#redirect the ntpdrift file
sudo sed -i -e 's#driftfile /var/lib/ntp/ntp.drift#driftfile /tmpfs/ntp.drift#g' /etc/ntp.conf

#New method of redirecting Opehabs logs
sudo sed -i -e 's#log4j2.appender.event.fileName = ${openhab.logdir}/events.log#log4j2.appender.event.fileName = /tmpfs/events.log#g' /var/lib/openhab2/etc/org.ops4j.pax.logging.cfg
sudo sed -i -e 's#log4j2.appender.out.fileName = ${openhab.logdir}/openhab.log#log4j2.appender.out.fileName = /tmpfs/openhab.log#g' /var/lib/openhab2/etc/org.ops4j.pax.logging.cfg
#List of files still doing writes
#/run/samba/msg.lock/ (multiple files)
#/run/samba/smbXsrv_client_global.tdb
#/var/cache/samba/browse.dat
# /var/lib/openhab2/config/org/eclipse/smarthome/storage/json.config //this file holds the configuration for the next few files...
#/var/lib/openhab2/jsondb/org.eclipse.smarthome.config.discovery.DiscoveryResult.json
#/var/lib/openhab2/jsondb/backup/ (multiple files)
#/var/lib/mosquitto/ (mosquitto.db and mosquitto.db.new) can be solved by editing /etc/mosquitto/mosquitto.conf and turn off persistence.

fi

################## MISC things that need doing #####
#Increase usable ram for java and also apply fix for system info binding.
sudo sed -i -e 's#EXTRA_JAVA_OPTS=""#EXTRA_JAVA_OPTS="-Xmx640m -Djna.library.path=/lib/arm-linux-gnueabihf/"#g' /etc/default/openhab2
#newer versions of Ubuntu do not run rc.local unless it is made runnable.
sudo sed -i '1 i #!/bin/sh' /etc/rc.local
sudo chmod +x /etc/rc.local
########### DISPLAY WELCOME MESSAGES HERE #######
echo
echo
echo "Congrats Openhab2 should now be installed. To start using Openhab2, open up this link in any browser http://$HostName:8080"
read -n1 -r -p "Press y if you would like to restart, or any other key to exit to the shell without restarting..." key
if [ "$key" == "y" ]; then
sudo reboot
fi
############### END OF SCRIPT ###################
